mapper.select:
AND title like concat('%', #{o.title}, '%') OR title_en like concat('%', #{o.title}, '%') OR title_id like concat('%', #{o.title}, '%')
AND business_type = #{o.businessType}
AND business_type in
#{o.businessType}
AND status = #{o.status}
AND oper_name like concat('%', #{o.operName}, '%')
and date_format(oper_time,'%y%m%d') >= date_format(#{o.params.beginTime},'%y%m%d')
and date_format(oper_time,'%y%m%d') <= date_format(#{o.params.endTime},'%y%m%d')
order by oper_id desc
