mapper.update:
 		update sys_post

 			post_code = #{p.postCode},

				post_name_en = 
				when "en" then #{p.postName}
				else post_name_en
				end,
				post_name = 
				when "zh" then #{p.postName}
				else post_name
				end,
				post_name_id = 
				when "id" then #{p.postName}
				else post_name_id
				end,

 			post_sort = #{p.postSort},
 			status = #{p.status},
 			remark = #{p.remark},
 			update_by = #{p.updateBy},
 			update_time = sysdate()

 		where post_id = #{p.postId}
